import telebot, subprocess
from telebot.types import Message
from googletrans import Translator
from telebot import types
import time


with open('token.txt', 'r') as file:
    token = file.read()


bot = telebot.TeleBot(token)


def trans_later(eng_text):
    translator = Translator()
    tr = translator.translate(eng_text, dest="ru")
    return tr.text


def analiz(src1):
    return (subprocess.run(
        f"pmd.bat check -d {src1} -R rulesets/java/quickstart.xml -f text",
        shell=True, capture_output=True, text=True, encoding='cp866').stdout)


def analiz_itog(message):
    with open('file.java', 'w', encoding='utf-8') as file:
        file.write(message.text)
    analizator_text = analiz() + '\n'
    analizator_text += "–ü–µ—Ä–µ–≤–æ–¥ –Ω–∞ —Ä—É—Å—Å–∫–∏–π —è–∑—ã–∫:" + '\n' + trans_later(analizator_text)
    bot.reply_to(message, analizator_text)


def print_file(f_name):
    with open(f_name, 'r', encoding='utf-8') as file:
        content = file.read()
    return content


@bot.message_handler(commands=['start'])
def start(message):
    markup = types.ReplyKeyboardMarkup(resize_keyboard=True)
    btn1 = types.KeyboardButton("–ú–µ—Ç–æ–¥—ã —Ä–µ—Ñ–∞–∫—Ç–æ—Ä–∏–Ω–≥–∞")
    btn2 = types.KeyboardButton("‚ùì –ó–∞–¥–∞—Ç—å –≤–æ–ø—Ä–æ—Å")
    markup.add(btn1, btn2)
    bot.send_message(message.chat.id,
                     text="–ü—Ä–∏–≤–µ—Ç, {0.first_name}! –Ø –±–æ—Ç –∫–æ—Ç–æ—Ä—ã–π —Ä–∞–∑–±–∏—Ä–∞–µ—Ç—Å—è –≤ java, –º–æ–≥—É —Ä–∞—Å—Å–∫–∞–∑–∞—Ç—å –æ –º–µ—Ç–æ–¥–∞—Ö —Ä–µ—Ñ–∞–∫—Ç–æ—Ä–∏–Ω–≥–∞ \n \n"
                          "–ï—Å–ª–∏ —Ö–æ—á–µ—à—å —á—Ç–æ–±—ã —è –ø—Ä–æ–∞–Ω–∞–ª–∏–∑–∏—Ä–æ–≤–∞–ª —Ç–≤–æ–π –∫–æ–¥ —Ç–æ –ø—Ä–æ—Å—Ç–æ –ø—Ä–∏—à–ª–∏ –º–Ω–µ –µ–≥–æ —Ç–µ–∫—Å—Ç–æ–º —Å–∫–æ–ø–∏—Ä–æ–≤–∞–≤ –µ–≥–æ –∏–∑ —Å–≤–æ–µ–≥–æ —Ä–µ–¥–∞–∫—Ç–æ—Ä–∞ –∏–ª–∏ –ø—Ä–∏—à–ª–∏ –º–Ω–µ —Ñ–∞–π–ª".format(
                         message.from_user), reply_markup=markup)


@bot.message_handler(content_types=['document'])
def get_text_messages(message: Message):
    file_info = bot.get_file(message.document.file_id)
    downloaded_file = bot.download_file(file_info.file_path)
    if message.document.file_name[-5:] == '.java':
        # src = f"file_{int(time.time())}.java"
        src = f"{message.document.file_name}"
        # src = f'C:/Users/korudenko/PycharmProjects/telegram_bot/{unique_filename}'
        with open(src, 'wb') as new_file:
            new_file.write(downloaded_file)
        analizator_text = analiz(src) + '\n'
        analizator_text += "–ü–µ—Ä–µ–≤–æ–¥ –Ω–∞ —Ä—É—Å—Å–∫–∏–π —è–∑—ã–∫:" + '\n' + trans_later(analizator_text)
        if len(analizator_text) > 4096:
            name_file2 = message.document.file_name[:-5] + "_–∞–Ω–∞–ª–∏–∑.txt"
            with open(name_file2, 'w', encoding='utf-8') as new_file1:
                new_file1.write(analizator_text)
            with open(name_file2, 'rb') as file3:
                bot.send_document(message.chat.id, file3, caption='–û—Ç–≤–µ—Ç '
                  '–∞–Ω–∞–ª–∏–∑–∞ –≤–∞—à–µ–≥–æ —Ñ–∞–π–ª–∞ –±–æ–ª—å—à–µ 4096 —Å–∏–º–≤–æ–ª–æ–º, –ø–æ—ç—Ç–æ–º—É –æ—Ç–ø—Ä–∞–≤–ª—è—é –í–∞–º —Ñ–∞–π–ª —Å –¥–∞–Ω–Ω–Ω—ã–º–∏')
        else:
            bot.reply_to(message, "–ò–º—è –ø—Ä–æ–∞–Ω–∞–ª–∏–∑–∏—Ä–æ–≤–∞–Ω–Ω–æ–≥–æ —Ñ–∞–π–ª–∞: " + message.document.file_name + '\n' + '\n' + analizator_text)
    else:
        bot.reply_to(message, "–§–∞–π–ª –Ω–µ –ø—Ä–æ–∞–Ω–∞–ª–∏–∑–∏—Ä–æ–≤–∞–Ω, —è –ø–æ–Ω–∏–º–∞—é —Ç–æ–ª—å–∫–æ —Ñ–∞–π–ª—ã –Ω–∞ –Ø–ü java")


